addListener('onClose', $.toString(() => {
    clearMyVar('intro');
    clearMyVar('voddata');
}));
rc('https://gitee.com/mistywater/hiker_info/raw/master/f', 24);
var d = [];
var html = fetchPC(MY_URL).replace(/http下载/g, 'HTTP');
var 片名 = pdfh(html, 'h1&&Text');
let 导演, 主演, 分类, 状态, 集数, 更新时间, 年代, 地区, 简介, 图片;

导演 = xpa(html, '//b[contains(text(),"角色：")]/..//text()').join('').replace(/\r|\n|\t/g, '');
主演 = xpa(html, '//b[contains(text(),"声优：")]/..//text()').join('').replace(/\r|\n|\t/g, '');
分类 = xpa(html, '//b[contains(text(),"类型：")]/..//text()').join('').replace(/\r|\n|\t/g, '');
状态 = '状态：' + xpa(html, '//b[contains(text(),"状态：")]/../text()[2]').join('').replace(/\r|\n|\t/g, '');
//集数 = xpa(html, '//span[contains(text(),"集数：")]/..//text()').join('').replace(/\r|\n|\t/g, '');
更新时间 = '更新时间：' + xpa(html, '//b[contains(text(),"状态：")]/../text()[1]').join('').replace(/\r|\n|\t/g, '');
年代 = '年代：' + xpath(html, '//b[contains(text(),"年代：")]/following-sibling::text()[1]');
地区 = '地区：' + xpath(html, '//b[contains(text(),"地区：")]/following-sibling::text()[1]');
图片 = pd(html, '.pic&&img&&src');
简介 = '简介：' + pdfh(html, '.comicnrr&&p&&Text').replace(/　/g, '');

d.push({
    title: ss((状态 ? 状态 : '') + (集数 ? '\n' + 集数 : '') + (更新时间 ? '\n' + 更新时间 : '') + (地区 ? '\n' + 地区 + (年代 ? '\t' + 年代 : '') : ''), 'CC3366'),
    desc: color((导演 ? 导演 : '') + (主演 ? '\n' + 主演.slice(0, 30) : '') + (分类 ? '\n' + 分类 : ''), '006699'),
    img: 图片,
    url: 'hiker://search?s=' + 片名 + '&rule=聚影√',
    col_type: 'movie_1_vertical_pic_blur',
    extra: {
        gradient: true
    }
});
eval(mline(5));
d.push({
    title: ss(简介, '9966AA').slice(0, 100) + '…',
    url: $('#noLoading#').lazyRule((简介) => {
        rc('https://gitee.com/mistywater/hiker_info/raw/master/f', 24);
        updateItem('intro', {
            title: ssR(简介, '9966AA'),
            col_type: 'rich_text'
        });
        updateItem('_intro', {
            title: strong('∧', 'CC0033'),
        });
        putMyVar('intro', '1');
        return 'hiker://empty';
    }, 简介),
    col_type: 'text_1',
    extra: {
        id: 'intro',
        lineVisible: false
    }
});
var intro = getMyVar('intro', '0');
d.push({
    title: intro == '0' ? strong('∨', '229977') : strong('∧', 'CC0033'),
    url: $('#noLoading#').lazyRule((简介) => {
        rc('https://gitee.com/mistywater/hiker_info/raw/master/f', 24);
        var intro = getMyVar('intro', '0');
        if (intro == '0') {
            updateItem('intro', {
                title: ssR(简介, '9966AA'),
                col_type: 'rich_text'
            });
            updateItem('_intro', {
                title: strong('∧', 'CC0033'),
            });
            putMyVar('intro', '1');
        } else {
            updateItem('intro', {
                title: ss(简介, '9966AA').slice(0, 100) + '…',
                col_type: 'text_1'
            });
            updateItem('_intro', {
                title: strong('∨', '229977')
            });
            putMyVar('intro', '0');
        }
        return 'hiker://empty';
    }, 简介),
    col_type: 'scroll_button',
    extra: {
        id: '_intro',
        videoExcludeRules: ['?url=']
    }
});
var 线路 = 'body&&.pfrom.tab1&&li';
var 选集 = 'body&&.urlli';
var 线路名 = 'Text';
var 选集列表 = 'body&&a';
var 排除 = '.* ';

var tabs = [];
var lists = [];
var arts = pdfa(html, 线路);
var conts = pdfa(html, 选集);
for (var i in arts) {
    tabs.push(pdfh(arts[i], 线路名));
}
storage0.putMyVar('tabs', tabs);
for (var i in conts) {
    lists.push(pdfa(conts[i], 选集列表));
}
storage0.putMyVar('lists', lists);
var listsPush = $.toString(() => ({
    title: getMyVar('ctype', '0') == '0' ? (pdfh(list, 'a&&Text') == '点击下载' ? pdfh(list, 'span&&Text') : pdfh(list, 'a&&Text')).replace(/.+?(第\d+季|第\d+集)/, '$1') : color(pdfh(list, 'a&&Text').replace(/.+?(第\d+季|第\d+集)/, '$1'), '555555'),
    url: $(pd(list, 'a&&href')).lazyRule((index) => {
        var url = '';
        if (/quark/.test(input)) {
            url = 'hiker://search?s=' + input + '&rule=uckk云';
        } else if (/aliyundrive/.test(input)) {
            url = 'hiker://search?s=' + input + '&rule=云盘汇影';
        } else if (/magnet|\.mp4|\.m3u8|\.flv/.test(input)) {
            url = input;
        } else {
            var html = fetchPC(input);
            if (/player_aaaa/.test(html)) {
                var html = JSON.parse(html.match(/r player_aaaa.*?=(.*?)</)[1]);
                var url_t = html.url;
                if (html.encrypt == '1') {
                    url_t = unescape(url_t);
                } else if (html.encrypt == '2') {
                    url_t = unescape(base64Decode(url_t));
                }
                if (/\.m3u8|\.mp4|\.flv/.test(url_t)) {
                    url = url_t;
                } else {
                    url = 'video://' + input;
                }
            } else {
                if (getMyVar('voddata')) {
                    var voddata = getMyVar('voddata');
                } else {
                    eval(html.match(/var VideoInfoList.*?\)/)[0].replace('base64decode', 'base64Decode'));
                    eval(getCryptoJS());
                    var key = CryptoJS.enc.Utf8.parse('Play20230626.com');
                    var iv = CryptoJS.enc.Utf8.parse('2023062620230626');
                    var decrypted = CryptoJS.AES.decrypt(VideoInfoList, key, {
                        iv: iv,
                        mode: CryptoJS.mode.CBC,
                        padding: CryptoJS.pad.ZeroPadding
                    });
                    var voddata = decrypted.toString(CryptoJS.enc.Utf8);
                    putMyVar('voddata', voddata);

                }
                var videolist = voddata.split("$$");
                var all = voddata.split("$$$");
                url = all[getMyVar(MY_URL, "0")].split("$$")[1].split("#")[index].split('$')[1];
                log(url);
                if (/bilibili/.test(url)) {
                    url = 'video://http://api.wpsseo.cn/?v=' + url;
                } else if (/\.m3u8|\.mp4|\.flv/.test(url)) {} else if (/http/.test(url)) {
                    url = 'video://' + url;
                } else {
                    url = "https://www.mddm.tv/insssss/player_html/php/urlxi.php?vid=" + url;
                    html = fetchPC(url, {
                        headers: {
                            referer: 'https://www.mddm.tv/insssss/player_html/mp4.html'
                        }
                    });
                    url = html.match(/url: ?\'http.*?\'/g).slice(0, 2).map(h => h.match(/(http.*?)\'/)[1]);
                    url = {
                        urls: url
                    };
                }
            }
        }
        return url;
    }, index),
    col_type: getMyVar('ctype', '0') == '0' ? 'text_3' : 'text_1',
    extra: {
        cls: 'lists',
        videoExcludeRules: ['goodjson', 'mp4.']
    }
}));
putMyVar('listsPush', listsPush);
var updateList = $.toString(() => {
    deleteItemByCls('lists');
    var s = [];
    var n = 0;
    for (var list of _list) {
        if (pdfh(list, 'a&&Text').replace(/.+?(第\d+季|第\d+集)/, '$1').length > 12) {
            n = n + 1;
        }
    }
    if (n > 3) {
        putMyVar('ctype', '1')
    }
    _list.forEach((list, index) => {
        eval('s.push(' + getMyVar('listsPush') + ');');
    });
    clearMyVar('ctype');
    addItemAfter('tabs' + (tabs.length - 1), s);
});
putMyVar('updateList', updateList);

function setTabs(tabs) {
    d.push({
        title: getMyVar('shsort', '0') == '1' ? strong('逆序', 'CC0033') : strong('正序', '229977'),
        url: $('#noLoading#').lazyRule(() => {
            rc('https://gitee.com/mistywater/hiker_info/raw/master/f', 24);
            var tabs = storage0.getMyVar('tabs');
            var lists = storage0.getMyVar('lists');
            let conf = getMyVar('shsort', '0');
            if (conf == '1') {
                var _list = lists[getMyVar(MY_URL, '0')];
                putMyVar('shsort', '0');
            } else {
                var _list = lists[getMyVar(MY_URL, '0')].reverse();
                putMyVar('shsort', '1');

            };
            updateItem('shsort', {
                title: getMyVar('shsort', '0') == '1' ? strong('逆序', 'CC0033') : strong('正序', '229977')
            });
            eval(getMyVar('updateList'));
            return 'toast://切换排序成功';
        }),
        col_type: 'scroll_button',
        extra: {
            id: 'shsort'
        }
    });
    for (var i = 0; i < tabs.length; i++) {
        d.push({
            title: getMyVar(MY_URL, '0') == i ? strong(tabs[i], 'f67280') : tabs[i],
            url: $('#noLoading#').lazyRule((i) => {
                rc('https://gitee.com/mistywater/hiker_info/raw/master/f', 24);
                putMyVar(MY_URL, i);
                var tabs = storage0.getMyVar('tabs');
                var lists = storage0.getMyVar('lists');
                for (var k in tabs) {
                    updateItem('tabs' + k, {
                        title: k == i ? strong(tabs[k], 'f67280') : tabs[k]
                    });
                }
                var _list = getMyVar('shsort', '0') == '0' ? lists[i] : lists[i].reverse();
                eval(getMyVar('updateList'));
                return 'hiker://empty';
            }, i),
            col_type: 'scroll_button',
            extra: {
                id: 'tabs' + i
            }
        });
    }
}

function setLists(lists) {
    var index = getMyVar(MY_URL, '0');
    var _list = getMyVar('shsort', '0') == '0' ? lists[index] : lists[index].reverse();
    var n = 0;
    for (var list of _list) {
        if (pdfh(list, 'a&&Text').replace(/.+?(第\d+季|第\d+集)/, '$1').length > 12) {
            n = n + 1;
        }
    }
    if (n > 3) {
        putMyVar('ctype', '1')
    }
    _list.forEach((list, index) => {
        eval('d.push(' + getMyVar('listsPush') + ');');
    });
    clearMyVar('ctype');
    if (_list.length <= 4) {
        eval(mblank(4));
    } else if (_list.length <= 8) {
        eval(mblank(3));

    } else if (_list.length <= 12) {
        eval(mblank(2));
    }
}

try {
    setTabs(tabs);
    setLists(lists);
} catch (e) {}
d.push({
    col_type: 'big_blank_block',
    extra: {
        id: 'lists'
    }
});


d.push({
    title: small('以上数据来源于网络，如您喜欢，请支持官方！', '871F78'),
    desc: small('此规则仅限学习交流使用，请于导入后24小时内删除，任何组织或个人不得以任何方式方法传播此规则的整体或部分！', 'F20C00'),
    url: 'hiker://empty',
    col_type: 'text_center_1',
    extra: {
        id: 'tabs0'
    }
});
setResult(d);

