    var serverTime = serverInfo.ServerTime;
    var kt = serverTime
    var kp = CryptoJS.MD5(serverTime + "{|}" + PlayId + "{|}" + PlayVid + "{|}" + 'agefans3382-getplay-1719').toString()
    var resGetPlay2 = fetch(serverInfo.Location +
        '?playid=' + encodeURI(PlayId) +
        '&vid=' + encodeURI(PlayVid) + '&kt=' + kt + '&kp=' + kp
    )
    var play = '';
