function urlParse(u) {
    var dict = {}
    dict.Source = u;
    let params = (url) => {
        let queryParams = {};
        let reg = /([^?=&]+)=([^?=&]+)/g;
        url.replace(reg, function() {
            queryParams[arguments[1]] = arguments[2];
        })
        return queryParams
    }
    var javaUrl = JavaImporter(java.net.URL);
    with(javaUrl) {
        var url = new URL(u);
        dict.Protocol = url.getProtocol();
        dict.Host = url.getHost();
        dict.Port = url.getPort();
        dict.File = url.getFile();
        dict.Path = url.getPath();
        dict.Ref = url.getRef();
    }
    dict.Params = params(u);
    return dict;
}

//扩展split,e是否去除空值
String.prototype.Split = function(s, e) {
    if (e) {
        return this.split(s).filter(item => item != '');
    } else {
        return this.split(s);
    }
}

//json对象转查询字符串
Object.ToParams = function() {
    var json = arguments[0];
    return Object.keys(json).map(key => key + '=' + json[key]).join('&');
}

//正则提取
RegExp.prototype.matchAll = function(string) {
    var match = null;
    var matches = new Array();
    while (match = this.exec(string)) {
        var matchArray = [];
        for (i in match) {
            if (parseInt(i) == i) {
                matchArray.push(match[i]);
            }
        }
        matches.push(matchArray);
    }
    return matches;
}

function getMaxLength(arr) {
    const maxLength = arr.reduce((acc, item) => Math.max(acc, item.length), 0);
    return maxLength
}

//左填充字符串
String.prototype.PadLeft = function(len, charStr) {
    var s = this + '';
    return new Array(len - s.length + 1).join(charStr, '') + s;
}

//右填充字符串
String.prototype.PadRight = function(len, charStr) {
    var s = this + '';
    return s + new Array(len - s.length + 1).join(charStr, '');
}

//重复字符串
let strRepeat = (s, i) => {
    return s.repeat(i);
};


function cutstr(str, len) {
    var str_length = 0;
    var str_len = 0;
    str_cut = new String();
    str_len = str.length;
    for (var i = 0; i < str_len; i++) {
        a = str.charAt(i);
        str_length++;
        if (escape(a).length > 4) {
            //中文字符的长度经编码之后大于4
            str_length++;
        }
        str_cut = str_cut.concat(a);
        if (str_length >= len) {
            //达到目标长度，即为字符串加上省略号并返回
            str_cut = str_cut.concat("...");
            return str_cut;
        }
    }
    //如果字符串长度小于需要裁切的长度，直接返回
    if (str_length < len) {
        return str;
    }
}"},{"col_type":"movie_3","name":"快捷搜索","path":"qsearchd","rule":"//搜索，以下基本不需要修改。
