function Decrypt(word,key,iv) {

    let decrypt = CryptoJS.AES.decrypt(word, CryptoJS.enc.Utf8.parse(key), {
        iv: CryptoJS.enc.Utf8.parse(iv),
        mode: CryptoJS.mode.CBC, // 使用CBC模式
        padding: CryptoJS.pad.Pkcs7 // 使用PKCS#7填充
    });

    // 将解密后的数据转换为原始文本
    let decryptedStr = decrypt.toString(CryptoJS.enc.Utf8);

    // 返回解密后的文本
    return decryptedStr;
}

const characterSet = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
//随机生成10位字符串
const randomString = Array.from({
    length: 10
}, () => characterSet[Math.floor(Math.random() * characterSet.length)]).join('');
//反转10位字符串用于s参数.后边拼接的字符串
const reversedString = randomString.split('').reverse().join('');

var s = md5('jocy' + '&' + t + '&' + randomString) + '.' + reversedString

var headers = {
    "s": s,
    "t": t,
    "x-token": token
}

function getUpcomingWeekDates() {
    // 创建一个空数组来存储日期
    let dates = [];
    // 循环遍历未来7天（从今天开始）
    for (let i = 0; i < 7; i++) {
        // 获取当前日期，并加上i天
        let date = new Date();
        date.setDate(date.getDate() + i);
        // 获取年、月、日
        let year = date.getFullYear();
        let month = (date.getMonth() + 1).toString().padStart(2, '0'); // 月份从0开始，所以加1
        let day = date.getDate().toString().padStart(2, '0'); // 确保日期是两位数
        // 组合年月日，并添加到数组中
        let formattedDate = year + '-' + month + '-' + day;
        dates.push(formattedDate); // 将日期添加到数组中
    }
    return dates; // 返回日期数组
}

function Transforweek(dates) {
    const daysOfWeek = ["星期日", "星期一", "星期二", "星期三", "星期四", "星期五", "星期六"];

    // 遍历日期数组并转换为对应的星期几
    const weekdays = dates.map(dateStr => {
        const date = new Date(dateStr); // 将字符串转换为 Date 对象
        const day = date.getDay(); // 获取星期几 (0-6 对应 星期日 到 星期六)
        return daysOfWeek[day]; // 返回对应的中文星期几
    });
    return weekdays
}
