        var html = JSON.parse(request(input).match(/r player_.*?=(.*?)</)[1]);
        var url = html.url;
        if (html.encrypt == '1') {
            url = unescape(url);
        } else if (html.encrypt == '2') {
            url = unescape(base64Decode(url));
        }

        function decrypt(encrypted) {
            try {
                return window0.atob(encrypted.split('').reverse().join(''));
            } catch (e) {
                console.error('解密失败：', e);
                return null;
            }
        }


        if (/dplayer|dyttm3u8|lzm3u8/.test(html.from)) {

            return url


        } else {
            eval(request(MY_HOME + '/static/js/playerconfig.js'));
            if (/mqifun/.test(html.from)) {
                var jx = MacPlayerConfig.player_list[html.from].parse;

            } else {
                var jx = MY_HOME + MacPlayerConfig.player_list[html.from].parse;
                if (jx == '') {
                    jx = MY_HOME + MacPlayerConfig.parse;
                }
                //log(jx)
            }


            var play = request(jx + url, {
                headers: {
                    'Referer': MY_HOME
                }
            });
            // log(play)
            if (/catplayer/.test(play)) {

                eval('var json=' + play.match(/window.__NUXT.*?=(.*?)</)[1])

                var mq = jx + url
                //log(mq)
                var mqjk = mq.split('?')[0] + '/vod/within/playaddr/get?' + mq.split('?')[1].split('&')[0]
                var video = JSON.parse(request(mqjk, {

                    headers: {
                        'request-url': json.state.video.requestUrl.split("//")[1]
                    }

                })).data.playAddr

                return JSON.stringify({

                    urls: video.map(video => 'https://162.14.98.254/vod' + video.addr),
                    names: video.map(video => video.title)
                }) + "#isVideo=true#";


            } else if (/ATQP|LMM|QFZH|wxv/.test(html.from)) {
                return "toast://暂时没搞，网页如能播放请反馈！"

            } else if (/tk/.test(html.from)) {

                function strdecode(string) {
                    key = md5('123456');
                    string = window0.atob(string);
                    len = key.length;
                    code = '';
                    for (i = 0; i < string.length; i++) {
                        k = i % len;
                        code += String.fromCharCode(string.charCodeAt(i) ^ key.charCodeAt(k));
                    }
                    return window0.atob(code);
                }
                var urls = eval(play.match(/quality:\s*(\[[\s\S]*?\])/)[1]);


                return JSON.stringify({
                    urls: urls.map(u => u.url),
                    names: urls.map(u => u.html)
                }) + "#isVideo=true#";



            } else if (/dm295|qifunqp/.test(html.from)) {
                let encryptedHtml = '';
                let encUrl = '';

                const m1 = play.match(/encryptedHtml = "(.*?)"/);
                if (m1) encryptedHtml = decrypt(m1[1]);

                const m2 = play.match(/encUrl = "(.*?)"/);
                if (m2) {
                    encUrl = decrypt(m2[1]);
                } else if (encryptedHtml) {
                    const m3 = encryptedHtml.match(/encUrl = "(.*?)"/);
                    if (m3) encUrl = decrypt(m3[1]);
                }

                return encUrl + '#isVideo=true#';
            }
        }

    },
    MY_HOME);



