    title: getMyVar('ctype', '0') == '0' ? (pdfh(list, 'a&&Text') == '点击下载' ? pdfh(list, 'span&&Text') : pdfh(list, 'a&&Text')).replace(/.+?(第\d+季|第\d+集)/, '$1') : color(pdfh(list, 'a&&Text').replace(/.+?(第\d+季|第\d+集)/, '$1'), '555555'),
    url: $(pd(list, 'a&&href')).lazyRule((index) => {
        var url = '';
        if (/quark/.test(input)) {
            url = 'hiker://search?s=' + input + '&rule=uckk云';
        } else if (/aliyundrive/.test(input)) {
            url = 'hiker://search?s=' + input + '&rule=云盘君.简';
        } else if (/magnet|\.mp4|\.m3u8|\.flv/.test(input)) {
            url = input;
        } else {
            var html = fetchPC(input);
            if (/player_aaaa/.test(html)) {
                var html = JSON.parse(html.match(/r player_aaaa.*?=(.*?)</)[1]);
                var url_t = html.url;
                if (html.encrypt == '1') {
                    url_t = unescape(url_t);
                } else if (html.encrypt == '2') {
                    url_t = unescape(base64Decode(url_t));
                }
                if (/\.m3u8|\.mp4|\.flv/.test(url_t)) {
                    url = url_t;
                } else {
                    url = 'video://' + input;
                }
