                    var key = CryptoJS.enc.Utf8.parse('Play20230626.com');
                    var iv = CryptoJS.enc.Utf8.parse('2023062620230626');
                    var decrypted = CryptoJS.AES.decrypt(VideoInfoList, key, {
                        iv: iv,
                        mode: CryptoJS.mode.CBC,
                        padding: CryptoJS.pad.ZeroPadding
                    });
                    var voddata = decrypted.toString(CryptoJS.enc.Utf8);
                    putMyVar('voddata', voddata);

