[{"col_type":"movie_3","name":"动态分类改","path":"dtfl","rule":"js:
/*
Ver 2023-10-28 1.0
备注：不要用海阔代码格式化 内容会出错
必须参数：
分类颜色 大类定位 
小类定位 分类标题 分类链接
html =>源码 page =>页码
可选参数：
拼接分类 折叠标记 展开标记 排除
大类链接 大类标题 清除键 开启折叠
附加定位
说明：
外面用getMyVar(MY_RULE.url) 或者 状态 取链接
魔改:α
*/

let empty = "hiker://empty";
let categories = [];
if (typeof (拼接分类) != 'undefined' && 拼接分类 != '') {
    categories = [pdfa(html, 大类定位).join('\n')];
    if (拼接分类.includes(';')) {
        拼接分类 = 拼接分类.split(';');
    }
    if (拼接分类.constructor == Array) {
        拼接分类.forEach(item => {
            categories = categories.concat(pdfa(html, item));
        })
    } else {
        categories = [pdfa(html, 大类定位).join('\n')].concat(pdfa(html, 拼接分类));
    }
    if (typeof (附加定位) != 'undefined' && 附加定位.length > 0) {
        附加定位.forEach(x => {
            var 一级 = pdfa(html, x.一级定位).map(s => {
                var 定位 = pdfa(s, x.小类).map(q => {
                    var title = pdfh(q, x.标题);
                    var url = 'a'
                    if (x.链接.includes('@@')) {
                        state = x.链接.split('@@')[0]
                        value = pdfh(q, x.链接.split('@@')[1]);
                        if (x.转换 == true) {
                            return `<li data-type="${state}" data-val="${value}"><a href="">${title}</a></li>`
                        } else {
                            return `<a type="@extra" state="${state}" value="${value}">${title}</a>`;
                        }
                    } else {
                        href = pd(q, x.链接);
                        return `<a href="${href}">${title}</a>`;
                    }
                });
                return 定位;
            });
            categories.push(一级.join('\n'));
        });
    }
} else {
    categories = [pdfa(html, 大类定位).join('\n')];
}

let items = [];
let cates = [];
let init_cate = new Array(20).fill("0");
const fold = getMyVar(MY_RULE.group, "0");
const cate_temp = JSON.parse(getMyVar("head.cate", JSON.stringify(init_cate)));
var 折叠状态 = (typeof (开启折叠) != 'undefined') ? 开启折叠 : true;
var 折叠 = (typeof (折叠标记) != 'undefined' && 折叠标记 != '') ? 折叠标记 : '““””' + '∨'.fontcolor("#FF0000");
var 展开 = (typeof (展开标记) != 'undefined' && 展开标记 != '') ? 展开标记 : '““””' + '∧'.fontcolor("#1aad19");
var 清除 = (typeof (清除键) != 'undefined' && 清除键 != '') ? 清除键 : [];
var 状态 = (typeof (状态标记) != 'undefined' && 状态标记 != '') ? 状态标记 : MY_RULE.url;
var 大类处理 = typeof (一级分类处理) != 'undefined' ? true : false;

var _动态返回=true;
if(typeof 动态返回!='undefined'&&typeof 动态返回 === "boolean"){
    _动态返回=动态返回;
}

var 搜索 = typeof (开启搜索) != 'undefined' ? 开启搜索 : false;
var 全清 = (typeof (全清键) != 'undefined' && 全清键 != '') ? 全清键 : [];
全清.unshift('yi_firstpage');


if (parseInt(page) === 1) {
    if (搜索) {
        d.push({
            title: "",
            url: "'hiker://search?rule=" + MY_RULE.title + "&s='+input",
            desc: "搜索关键词",
            col_type: "input",
            extra: {
                defaultValue: getMyVar('keyword', ''),
                onChange: $.toString(() => {
                    putMyVar('keyword', input);
                })
            }
        })
    }
    if (page == 1 ) {
    if (折叠状态) {
          items.push({
                title: fold === '1' ? 折叠 : 展开,
                url: $("#noLoading#").lazyRule((title, fold, u, all, exps) => {
                    var fold = getMyVar(MY_RULE.group, fold);
                    if(getMyVar('cate_empty','empty')=='empty'){
                      return 'toast://拼接部分为空';
                    }

                    var [展开, 折叠] = exps;
                    if (fold == 1) {
                        var cx = findItemsByCls(title + 'cate_button');
                        storage0.putMyVar('cate_button', cx);
                        deleteItemByCls(title + 'cate_button');
                    } else {
                        var cx = storage0.getMyVar('cate_button', []).map(x => {
                            x['col_type'] = x['col_type'] ? x['col_type'] : x['type'];
                            return x;
                        });
                        addItemAfter(title + 'cate_line', cx)
                    }
                    /*all.forEach((item) => {
                      clearMyVar(item);
                    });*/
                    updateItem(title + 'cate_exp', {
                        title: fold == 1 ? 展开 : 折叠,
                    })
                    putMyVar(MY_RULE.group, fold === "1" ? "0" : "1");
                    return "hiker://empty";
                }, MY_RULE.title, fold, 状态, 全清, [展开, 折叠]),
                col_type: 'scroll_button',
                extra: {
                    id: MY_RULE.title + 'cate_exp',
                    longClick: [{
                        title: '清除状态',
                        js: $.toString((x, all, cl) => {
                            clearMyVar(x);
                            clearMyVar('head.cate');
                            all.forEach((item) => {
                                clearMyVar(item)
                            })
                            cl.forEach((item) => {
                                clearMyVar(item)
                            })
                            refreshPage();
                        }, 状态, 全清, 清除)
                    }]
                }
            })
    }else{
       var bo=JSON.parse(getMyVar('head.cate','[]')).every(x=>x==="0");
       if(!bo){
       items.push({
         title:'““””'+'♻'.fontcolor('#3CB371'),
         col_type:'scroll_button',
         url:$('#noLoading#').lazyRule((x, all, cl)=>{
           clearMyVar(x);
           clearMyVar('head.cate');
           all.forEach((item) => {
             clearMyVar(item)
          })
            cl.forEach((item) => {
             clearMyVar(item)
           })
           refreshPage(false);
           return 'hiker://empty';
         }, 状态, 全清, 清除),
       })
      }}
    }
    let setTitle = (k, i, t) => {
        return k == i ? "““””" + t.fontcolor(分类颜色) : t
    }
    categories.forEach((category, index) => {
        let sub_categories = [];
        if (小类定位.includes(';')) {
            sub_categories = pdfa(category, 小类定位.split(';')[0]);
            if (sub_categories.length == 0) {
                sub_categories = pdfa(category, 小类定位.split(';')[1]);
            }
        } else {
            sub_categories = pdfa(category, 小类定位);
        }
        if (index === 0) {
            sub_categories.forEach((item, key) => {
                let _大类链接 = (typeof (大类链接) != 'undefined' && 大类链接 != '') ? 大类链接 : 分类链接;

                let _大类标题 = (typeof (大类标题) != 'undefined' && 大类标题 != '') ? 大类标题 : 分类标题;

                let title = pdfh(item, _大类标题);
                if (typeof (排除) != 'undefined' && 排除 != '') {
                    title = title.replace(new RegExp(排除, "g"), "");
                }
                let params = {
                    cate_temp: cate_temp, key: key, page: page, clears: 清除, state: 状态, allclears: 全清
                }

                let url = pd(item, _大类链接);
                if (大类处理) {
                    //log(一级分类处理)
                    //log($.stringify(一级分类处理))
                    let input = url;
                    eval("result = " + 一级分类处理);
                    //log(result)
                    url = result;
                }
                items.push({
                    title: setTitle(key.toString(), cate_temp[index], title),
                    col_type: 'scroll_button',
                    url: $(url + '#noLoading#').lazyRule((params) => {
                        let new_cate = [];
                        params.clears.forEach((item) => {
                            clearMyVar(item)
                        })
                        params.allclears.forEach((item) => {
                            clearMyVar(item)
                        })

                        params.cate_temp.forEach((cate, index) => {
                            new_cate.push(index === 0 ? params.key.toString() : "0")
                        })
                        putMyVar("head.cate", JSON.stringify(new_cate));
                        putMyVar(params.state, input);
                        refreshPage(true);
                        return "hiker://empty";
                    }, params)
                });
            });
            items.push({
                col_type: "blank_block",
                extra: {
                    id: MY_RULE.title + 'cate_line',
                }
            })
        } else {
            sub_categories.forEach((item, key) => {
                var extrapos = false;
                if (item.includes('type="@extra"')) {
                    extrapos = true;
                    //log('附加定位')
                }

                if (extrapos) {
                    var title = pdfh(item, 'a&&Text');
                    if (typeof (排除) != 'undefined' && 排除 != '') {
                        title = title.replace(new RegExp(排除, "g"), "");
                    }
                    var json = {
                        type: pdfh(item, 'a&&state'),
                        value: pdfh(item, 'a&&value'),
                    }
                } else {

                    var title = pdfh(item, 分类标题);
                    if (typeof (排除) != 'undefined' && 排除 != '') {
                        title = title.replace(new RegExp(排除, "g"), "");
                    }

                    var json = '';
                    switch (true) {
                        case 分类链接.includes('@@@'):
                            json = {
                                type: pdfh(item, 分类链接.split('@@@')[0]),
                                value: pdfh(item, 分类链接.split('@@@')[1]),
                            }
                            break;
                        case 分类链接.includes('@@'):
                            json = {
                                type: 分类链接.split('@@')[0],
                                value: pdfh(item, 分类链接.split('@@')[1]),
                            }
                            break;
                        default:
                            json = pd(item, 分类链接);
                            break;
                    }
                    /*var json=分类链接.includes('@@@')?{
                         type:pdfh(item,分类链接.split('@@@')[0]),
                         value:pdfh(item,分类链接.split('@@@')[1]),
