                }
                cates.push({
                    title: setTitle(key.toString(), cate_temp[index], title),
                    url: $('hiker://empty' + '#noLoading#').lazyRule((params) => {
                        params.allclears.forEach((item) => {
                            clearMyVar(item)
                        })
                        params.cate_temp[params.index] = params.key.toString();
                        putMyVar("head.cate", JSON.stringify(params.cate_temp));
                        if (params.json.constructor === Object) {
                            putMyVar('dt_' + params.json.type, params.json.value);
