}
var weeks = new Array("周日", "一", "二", "三", "四", "五", "六");

function getWeekDate() {
    var now = new Date();
    var day = now.getDay();
    var week = weeks[day];
    //log(week)
    return week;
}
let now_Week = getWeekDate();

