        if (/周|星期/.test(wk)) {
            wk = wk.replace(/周|星期/, "")
        }
        var map = {
            "日": 0,
            "一": 1,
            "二": 2,
            "三": 3,
            "四": 4,
            "五": 5,
            "六": 6
        };
        return map[wk]
    }
    let wk_num = wkTonum(chose_week);
