var 状态 =
typeof 状态标记 != "undefined" && 状态标记 != "" ? 状态标记: MY_RULE.url;

function getTruelink(mark, url, page) {
  var p = getMyVar(mark, url).replace("fypage", page);
  if (page == 1) {
    return getMyVar("yi_firstpage", p);
  } else if (p != "") {
    return p;
  } else {
    return url;
  }
}

function autoPage() {
  if (page == 1) {
    putMyVar("yi_firstpage", true_url);
  }
  try {
    var next = eval(下一页);
    //log("next");
    //log('next:'+next);
  } catch {
    log("可能不存在下一页或者下一页定位有问题");
    next = "";
  }
  if (next != "") {
    putMyVar(状态, next);
  }
}"}]

