function decrypt(encryptedText) {
    // è§£ç  Base64 åŠ å¯†æ–‡æœ¬
    const encrypted = CryptoJS.enc.Base64.parse(encryptedText);

    // æå– IV å’Œ å¯†æ–‡
    const iv = CryptoJS.lib.WordArray.create(encrypted.words.slice(0, 4));
    const ciphertextHex = CryptoJS.enc.Hex.stringify(CryptoJS.lib.WordArray.create(encrypted.words.slice(4)));

    // è®¾ç½®å¯†é’¥
    const key = CryptoJS.enc.Hex.parse("355626526f52254a6640704a50796446");

    // AES è§£å¯†
    const decrypted = CryptoJS.AES.decrypt({ ciphertext: CryptoJS.enc.Hex.parse(ciphertextHex) }, key, { iv });

    // è½¬æ¢è§£å¯†ç»“æœ
    const decryptedText = decrypted.toString(CryptoJS.enc.Utf8);

    // è¿”å› JSON è§£æç»“æœ
    return JSON.parse(decryptedText);
}"}]

